import './App.css';
import Navbar from './components/Navbar';
import TextForm from './components/TextForm';
import React, { useState } from 'react';
import Alert from './components/Alert';
//import About from './components/About';



function App() {
  const [mode,setMode]  = useState('light');   // whether dark mode is enabled or not
  const [alert, setalert]= useState(null);

  const showAlert = (message, type)=>{
    setAlert({
      message: message,
      type:type
    })
  }
  const toggleMode =()=>{
  if(mode==='light'){
  setMode('dark');
  document.body.style.backgroundColor = '#2c3b4c';
  setAlert("Dark mode has been enabled", "success");
}

else{
  setMode('light');
  document.body.style.backgroundColor = 'white';
  setAlert("light mode has been enabled", "success");
}
}
return (
<>
{/* {<Navbar title="Hacker" aboutText="About Hacker"/>}
{<Navbar/>} */}
<Navbar title="Hacker" mode={mode} toggleMode={toggleMode}/>
<Alert alert={alert}/>
<div className="container my.3">
<TextForm heading="Enter the text to analyze below" mode={mode}/>
{/* <About/> */}
</div>
    </>
  );
}


export default App;
